mapper.select:
select id , password , name , email , nvl( birthDay , '(미설정)') as birthDay ,nvl( GENDER , '(미설정)') as gender
,nvl( PHONE , '(미설정)') as phone ,nvl( ZIPCODE , '(미설정)') as zipcode ,nvl( ADDRESS1 , '(미설정)') as address1 
,nvl( ADDRESS2 , '(미입력)') as address2, type , point from member where id=#{id}

mapper.update:
update member set name=#{name}

		,birthday = #{birthDay}


		,gender = #{gender}


		,phone = #{phone}


		,zipcode = #{zipcode}


		,address1 = #{address1}


		,address2 = #{address2}

where id=#{id}

update member set point = #{point}
where id = (select id 
			from (select row_number() over(order by id asc) as rown , member.* from member) 
			where rown = #{index})
