mapper.select:
select * from 


		(select row_number() over(order by write_date desc) rown, review_comments.* from review_comments where review_seq = #{seq}) 


		(select row_number() over(order by rc.write_date desc) rown, rc.* , rcl.ID as likeOk 
		from review_comments rc , (select * from review_comments_likes where id=#{id}) rcl where rc.seq = rcl.R_COMMENTS_SEQ(+) and rc.review_seq = #{seq}) 


 where rown between #{start} and #{end}
